function upTime(e){now=new Date,e=new Date(e),difference=now-e,days=Math.floor(difference/864e5*1),hours=Math.floor(difference%864e5/36e5*1),mins=Math.floor(difference%864e5%36e5/6e4*1),secs=Math.floor(difference%864e5%36e5%6e4/1e3*1),document.getElementById("days").firstChild.nodeValue=days,document.getElementById("hours").firstChild.nodeValue=hours,document.getElementById("minutes").firstChild.nodeValue=mins,document.getElementById("seconds").firstChild.nodeValue=secs,clearTimeout(upTime.to),upTime.to=setTimeout(function(){upTime(e)},1e3)}